mapper.select:
SELECT A_DATE, G_NO, U_ID, A_FLAG, A_DEADLINE
FROM   C_ATTEND
WHERE  G_NO = #{g_no}
AND    U_ID = #{u_id}
ORDER BY A_DATE ASC

mapper.update:
Update C_ATTEND
SET    A_FLAG = 3
WHERE  A_DATE = TO_DATE(#{a_date},'YYYY-MM-DD HH24:MI:SS')
AND	   U_ID = #{u_id}
AND	   G_NO = #{g_no}
AND	   A_FLAG = 0


Update C_ATTEND
SET    A_FLAG = 3
WHERE  U_ID = #{u_id}
AND	   A_DEADLINE < SYSDATE
AND	   G_NO = #{g_no}
AND	   A_FLAG = 0


Update C_ATTEND
SET    A_FLAG = 1
WHERE  U_ID = #{u_id}
AND    A_DEADLINE = TO_DATE(#{a_deadLine},'YYYY-MM-DD HH24:MI:SS')
AND	   A_DEADLINE > SYSDATE
AND	   G_NO = #{g_no}

mapper.select:
		SELECT *
		FROM   C_ATTEND  
		WHERE  U_ID = #{u_id}
 		AND    G_NO = #{g_no}
		AND	   A_DEADLINE = #{a_deadLine}

SELECT A_DATE, G_NO, U_ID, A_FLAG
FROM   C_ATTEND
WHERE  U_id = #{u_id}
AND    G_NO = #{g_no}
AND    A_FLAG = #{a_flag}
