mapper.insert:
INSERT INTO C_HW ( h_no
					, u_id
					, g_no
					, h_title
					, h_content
					, h_file
					, h_date
					, h_flag
					, h_deadline
					, h_score)
			VALUES ( h_no_seq.NEXTVAL
					, #{u_id}
					, #{g_no}
					, #{h_title}
					, #{h_content}
					, #{h_file}
					, SYSDATE
					, 0
					, #{h_deadline}
					, #{h_score}
					 )

INSERT INTO C_HW ( h_no
					, u_id
					, g_no
					, h_title
					, h_content
					, h_file
					, h_date
					, h_flag
					, h_score)
			VALUES ( #{h_no}
					, #{u_id}
					, #{g_no}
					, #{h_title}
					, #{h_content}
					, #{h_file}
					, SYSDATE
					, 1
					, 0
					 )

mapper.select:

SELECT H_NO, G_NO, U_ID, H_TITLE, H_CONTENT, H_FILE, H_DATE, H_FLAG, H_SCORE
FROM C_HW
WHERE H_NO = #{h_no} AND H_FLAG = 1 AND U_ID = #{u_id}

mapper.delete:
UPDATE C_HW 
SET H_FLAG = 2 
WHERE H_NO = #{h_no}
