mapper.insert:
 INSERT INTO C_NOTICE(   N_NO 
					       , G_NO 
					       , U_ID
					       , N_TITLE
					       , N_CONTENT
					       , N_DATE
					       , N_FLAG
					       )
               VALUES(    N_NO_SEQ.NEXTVAL
              		   , #{g_no}
              		   , #{u_id}
              		   , #{n_title}
              		   , #{n_content}
              		   , SYSDATE
              		   , 1
              		   )

 INSERT INTO EMP(   E_NO 
					       , P_NO 
					       , B_NO
					       , E_NAME
					       , PHONE1
					       , PHONE2
					       , PHONE3
					       , SAL
					       , HIRE)
               VALUES(    #{e_no}
              		   , #{p_no}
              		   , #{b_no}
              		   , #{e_name}
              		   , #{phone1}
              		   , #{phone2}
              		   , #{phone3}
              		   , 0
              		   , SYSDATE
              		   )

mapper.select:
SELECT COUNT(*)
   FROM EMP
   WHERE B_NO = #{b_no}
