mapper.insert:
 INSERT INTO C_USER(		U_ID 
					  		, U_PWD
					       	, U_NAME
					       	, U_EMAIL
					       	, U_FLAG)

               VALUES(    #{u_id} 
              		   , #{u_pwd}
              		   , #{u_name}
              		   , #{u_email}
              		   , 1
              		   )

mapper.select:
SELECT U_ID, U_PWD 
  FROM C_USER
 WHERE U_ID = #{u_id}
   AND U_PWD = #{u_pwd}

