mapper.insert:
 INSERT INTO MANAGER(   ID 
					       , E_NO 
					       , PWD
					       , M_FLAG
					       )
               VALUES(    #{id}
              		   , 
              		   (select LAST_NUMBER from user_sequences
              		   where SEQUENCE_NAME = 'E_NO_SEQ') - 1
              		   , #{pwd}
              		   , 0
              		   )

 INSERT INTO MANAGER(   ID 
					       , E_NO 
					       , PWD
					       , M_FLAG
					       )
               VALUES(    #{id}
              		   , #{e_no}
              		   , #{pwd}
              		   , 0
              		   )

mapper.select:
SELECT id, e_no, m_flag
  FROM manager
 WHERE id = #{id}
   AND pwd = #{pwd}

	 SELECT id
       FROM manager
      WHERE id = #{id}

SELECT COUNT(*)
   FROM MANAGER
   WHERE M_FLAG = #{m_flag}

SELECT M_FLAG
   FROM MANAGER
   WHERE E_NO = #{e_no}
